import React, { Component } from "react";
import { Link, Route } from "react-router-dom";

import Detail from "./Detail";

export default class Message extends Component {
  state = {
    messageArr: [
      { id: "01", title: "消息1" },
      { id: "02", title: "消息2" },
      { id: "03", title: "消息3" },
    ],
  };

  replaceShow = (id,title) => {
    //编写一段代码，让其实现跳转到Detail组件，且为replace跳转
    //即手动操作history对象

    this.props.history.replace(`/home/message/detail/${id}/${title}`)

  }

  render() {
    const { messageArr } = this.state;
    return (
      <div>
        <ul>
          {messageArr.map((msgObj) => {
            return (
              <li key={msgObj.id}>
                {/* 向路由组件传递params参数 */}
                <Link to={`/home/message/detail/${msgObj.id}/${msgObj.title}`}>{msgObj.title}</Link>
                `&nbsp;`<button>push查看</button>
                `&nbsp;`<button onClick={()=>this.replaceShow(msgObj.id,msgObj.title)}>replace查看</button>



                {/* 向路由组件传递search参数，像是query*/}
                {/* <Link
                  to={`/home/message/detail/?id=${msgObj.id}&title=${msgObj.title}`}
                >
                  {msgObj.title}
                </Link> */}

                {/* 向路由组件传递state参数 */}
                {/* 传递一个对象给to，并且注意是pathname */}
                {/* <Link replace={true} to={{pathname:'/home/message/detail',state:{id:msgObj.id,title:msgObj.title}}}>
                  {msgObj.title}
                </Link> */}


              </li>
            );
          })}
        </ul>
        <hr />

        {/* 注册路由 */}

        {/* 声明接收params参数 */}
        <Route path="/home/message/detail/:id/:title" component={Detail}></Route>

        {/* search参数无需声明接收，正常注册路由即可*/}
        {/* <Route path="/home/message/detail" component={Detail}/> */}

         {/* state参数无需声明接收，正常注册路由即可*/}
        {/* <Route path="/home/message/detail" component={Detail}/> */}
      </div>
    );
  }
}
